import React from 'react'

function UpcomingEvents() {
    const [verifiedList, setVerifiedList] = useState([]);
    useEffect(() => {
        Axios.get('http://localhost:3001/getVerifiedEvents')
            .then((res) => {
                setVerifiedList(res.data);
            })
            .catch((err) => {
                alert(err);
            })
    }, [setVerifiedList]);
    return (
        <div>
            <h2>UpcomingEvents</h2>
            <div>
                {
                    verifiedList.map((ele) => {
                        return (
                            <div key={ele.eventid}>
                                <h4>{ele.ename}</h4>
                                <div>{ele.desc}</div>
                                <div>{ele.edate}</div>
                                <a href={ele.reglink}>Click here to register</a>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default UpcomingEvents
