import React, { useEffect, useState } from 'react'
import Axios from 'axios';
import { useLocation } from 'react-router-dom';
import '../style/Event.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Event() {
  const location = useLocation();
  const [eventData, setEventdata] = useState([]);
  const [userid, setUserid] = useState("");
  const [isAdmin, setIsadmin] = useState(false);
  useEffect(() => {
    setEventdata(location.state.event);
    setUserid(location.state.userid);
    if (userid === "admin") {
      setIsadmin(true);
    }
  }, [setEventdata, setUserid, location, setIsadmin, userid]);
  const handleRegistered = () => {
    Axios.post('http://localhost:3001/addRegister', {
      event: eventData,
      userid: userid
    })
      .then((res) => {
        if (res.data === "Success") {
          toast.success("Redirecting to Orignal website...");
        } else {
          toast.warn("Cannot register...");
        }
      })
  }
  return (
    <div className='event-cont'>
      <div className='event-data'>
        <div className='event-details'>
          <div>
            <h1>{eventData.ename}</h1>
            <h4> â€¢ {eventData.eventid}</h4>
          </div>
          <div>
            <h4>Description</h4>
            <div>{eventData.desc}</div>
          </div>
          <div>
            <h4>Date Of Event</h4>
            <div>{eventData.edate}</div>
          </div>
          <div>
            <h4>Venue</h4>
            <div>{eventData.venue}</div>
          </div>
          <div>
            <h4>Form</h4>
            <input placeholder='Enter Full Name'/>
            <input placeholder='Enter Email ID'/>
            <input placeholder=''/>
          </div>
          {
            isAdmin ? null : <a className='reglink-btn' href={`${eventData.reglink}`} onClick={handleRegistered}>Register</a>
          }
        </div>

      </div>
      <ToastContainer autoClose={2000} />
    </div>
  )
}

export default Event
