import React, { useEffect, useState } from 'react'
import Axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';

function UpcomingEvents({search, userid}) {
    const [verifiedList, setVerifiedList] = useState([]);
    const navigate = useNavigate();
    const location = useLocation();
    useEffect(() => {
        Axios.get('http://localhost:3001/getVerifiedEvents')
            .then((res) => {
                setVerifiedList(res.data);
            })
            .catch((err) => {
                alert(err);
            })
    }, [setVerifiedList]);
    const handleEventClick = (event) => {
        navigate('/event',{state:{userid,event}});
    }
    return (
        <div>
            <h2>UpcomingEvents</h2>
            <div>
                {
                    verifiedList.filter((item)=>{
                        return search.toLowerCase() === '' ? item : item.ename.toLowerCase().includes(search);
                    })
                    .map((ele) => {
                        return (
                            <div key={ele.eventid} onClick={() => handleEventClick(ele)}>
                                <h4>{ele.ename}</h4>
                                <div>{ele.desc}</div>
                                <div>{ele.edate}</div>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default UpcomingEvents
