import React, { useEffect, useState } from 'react';
import Axios from 'axios';

function Schedule({userid}) {
    const [eventid, setEventid] = useState("");
    const [ename, setEname] = useState("");
    const [desc, setDesc] = useState("");
    const [edate, setEdate] = useState("");
    const [venue, setVenue] = useState("");
    const [reglink, setReglink] = useState("");
    const [verified, setVerified] = useState(false);
    useEffect(() => {
        setVerified(false);
    }, [setVerified]);
    const handleCreateEventBtn = () => {
        if (eventid !== "" && ename !== "" && desc !== "" && edate !== "" && venue !== "" && reglink !== "") {
            Axios.post('http://localhost:3001/createEvent', {
                    eventid: eventid,
                    ename: ename,
                    creatorid: userid,
                    desc: desc,
                    edate: edate,
                    venue: venue,
                    reglink: reglink,
                    verified: verified
                }).then((res)=>{
                    if (res.data === "Success"){
                        alert("Event scheduled... Should be verified by Admin...");
                    } else if (res.data === "Error"){
                        alert("Cannot Add Event...");
                    }
                })
        } else {
            alert("Enter all details...");
        }
    }
    return (
        <div className=''>
            <h2>Schedule Events</h2>
            <div className=''>
                <input
                    required
                    placeholder='Enter eventid'
                    type='text'
                    name='eventid'
                    onChange={(e) => {
                        setEventid(e.target.value);
                    }}
                />
                <input
                    placeholder='Enter event name'
                    type='text'
                    name='ename'
                    onChange={(e) => {
                        setEname(e.target.value);
                    }}
                />
                <input
                    placeholder='Enter event description'
                    type='text'
                    name='desc'
                    onChange={(e) => {
                        setDesc(e.target.value);
                    }}
                />
                <input
                    placeholder='Enter event date'
                    type='text'
                    name='edate'
                    onChange={(e) => {
                        setEdate(e.target.value);
                    }}
                />
                <input
                    placeholder='Enter registeration link'
                    type='text'
                    name='reglink'
                    onChange={(e) => {
                        setReglink(e.target.value);
                    }}
                />
                <input
                    placeholder='Enter venue'
                    type='text'
                    name='venue'
                    onChange={(e) => {
                        setVenue(e.target.value);
                    }}
                />
                <button onClick={handleCreateEventBtn}>Create Event</button>
            </div>
        </div>
    )
}

export default Schedule
