import React, { useEffect, useState } from 'react'
import Axios from 'axios'

function UpdateUser() {
    const [toUpdate, setToupdate] = useState(false);
    const [toSearchUser, setTosearchuser] = useState("");
    const [clicktoInput,setClicktoinput] = useState(false);
    const handleToUpdateCheck = () => {
        setToupdate(!toUpdate);
    }
    const [userList, setUserList] = useState([]);
    useEffect(() => {
        Axios.get('http://localhost:3001/getUsersList')
            .then((res) => {
                setUserList(res.data);
            })
            .catch((err) => {
                alert(err);
            })

    }, [setUserList]);
    const handleOpenInput = () => {
        setClicktoinput(!clicktoInput);
    }
    return (
        <div>
            <button onClick={handleToUpdateCheck}>Update User</button>
            {
                toUpdate ?
                    <div>
                        <input
                            placeholder='Search user...'
                            type='text'
                            onChange={(e) => {
                                setTosearchuser(e.target.value);
                            }}
                        />
                        <div>
                            {
                                userList.filter((item)=>{
                                    return toSearchUser === '' ? null : item.userid.startsWith(toSearchUser);
                                })
                                .map((ele)=>{
                                    return (
                                        <div key={ele._id}>
                                            <h5 onClick={handleOpenInput}>{ele.fname} -- {ele.userid} -- {ele.role} -- {ele.email}</h5>
                                            {
                                                clicktoInput ? <input placeholder='Change Role'/> : null
                                            }
                                        </div>
                                    )
                                })
                            }
                        </div>
                    </div>
                    :
                    null
            }
        </div>
    )
}

export default UpdateUser
