import React, { useEffect, useState } from 'react'
import Axios from 'axios';
import { useLocation } from 'react-router-dom';
import '../style/Event.css';

function Event() {
  const location = useLocation();
  const [eventData, setEventdata] = useState([]);
  const [userid, setUserid] = useState("");
  const [isAdmin, setIsadmin] = useState(false);
  useEffect(() => {
    setEventdata(location.state.event);
    setUserid(location.state.userid);
    if (userid === "admin") {
      setIsadmin(true);
    }
  }, [setEventdata, setUserid, location, setIsadmin, userid]);
  const handleRegistered = () => {
    Axios.post('http://localhost:3001/addRegister', {
      event: eventData,
      userid: userid
    })
      .then((res) => {
        if (res.data === "Success") {
          alert("Redirecting to Orignal website...");
        } else {
          alert("Do not register...");
        }
      })
  }
  return (
    <div className='event-cont'>
      <div className='event-data'>
        <div className='event-details'>
        <h1>{eventData.ename}</h1>
        <div>
          EventID <span style={{fontWeight:'bold'}}>[{eventData.eventid}]</span>
          </div>
        <div>Description {eventData.desc}</div>
        {
          isAdmin ? null : <a href={`${eventData.reglink}`} onClick={handleRegistered}>Click here to Register</a>
        }
        </div>
        
      </div>
    </div>
  )
}

export default Event
