import React, { useEffect, useState } from 'react'
import Axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import '../style/Event.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Event() {
  const location = useLocation();
  const navigate = useNavigate();
  const [eventData, setEventdata] = useState([]);
  const [userid, setUserid] = useState("");
  const [isAdmin, setIsadmin] = useState(false);
  const [fRegname, setFregname] = useState("");
  const [emailReg, setEmailreg] = useState("");
  const [isDisabled,setIsdisabled] = useState(true);
  const [userData,setUserdata] = useState([]);
  useEffect(() => {
    setEventdata(location.state.event);
    setUserid(location.state.userid);
    if (userid === "admin") {
      setIsadmin(true);
    }
  }, [setEventdata, setUserid, location, setIsadmin, userid]);
  const handleRegistered = () => {
    if (emailReg !== "" && fRegname !== "") {
      setIsdisabled(false);
      Axios.post('http://localhost:3001/addRegister', {
        event: eventData,
        userid: userid
      })
        .then((res) => {
          if (res.data === "Success") {
            toast.success("Redirecting to Orignal website...");
          } else {
            toast.warn("Cannot register...");
          }
        })
    } else {
      setIsdisabled(true);
      toast.warn("Enter all Details!");
    }
  }
  const handleBackHome = () => {
    Axios.post('http://localhost:3001/getUserData',{
      userid:userid
    })
    .then((res)=>{
      setUserdata(res.data);
    })
    navigate('/home',{state:{userData}});
    console.log(userData);
  }
  return (
    <div className='event-cont'>
      <div className='event-data'>
      <button className='loginbtn' onClick={handleBackHome()}>Back to home</button>
        <div className='event-details'>
          <div>
            <h1>{eventData.ename}</h1>
            <h4> • {eventData.eventid}</h4>
          </div>
          <div>
            <h4>Description</h4>
            <div>{eventData.desc}</div>
          </div>
          <div>
            <h4>Date Of Event</h4>
            <div>{eventData.edate}</div>
          </div>
          <div>
            <h4>Venue</h4>
            <div>{eventData.venue}</div>
          </div>
          {
            isAdmin ? null :
              <div className='one-event profile-event-card form-fill'>
                <h4>Form</h4>
                <input placeholder='Enter Full Name' onChange={(e) => {setFregname(e.target.value)}} />
                <input placeholder='Enter Email ID' onChange={(e) => {setEmailreg(e.target.value)}} />
                <a className='reglink-btn' rel='noreferrer' target='_blank' href={`${eventData.reglink}`} onClick={handleRegistered}>
                  <img alt='Redirect Logo' className='redirect-logo' src={require('../images/redirect.png')} />
                  <div>Register</div>
                </a>
              </div>
          }
        </div>

      </div>
      <ToastContainer autoClose={2000} />
    </div>
  )
}

export default Event
