import React, { useEffect, useState } from 'react'
import Axios from 'axios';

function VerifyEvents() {
    const [toVerifylist,setToverifylist] = useState([]);
    useEffect(() => {
        Axios.get('http://localhost:3001/getNoneVerifiedEvents')
        .then((res)=>{
            setToverifylist(res.data);
        })
        .catch((err)=>{
            alert(err);
        })
    }, [setToverifylist]);
    const handleVerifyIt = (eventid) => {
        Axios.post('http://localhost:3001/verifyEvent',{
            eventid:eventid
        })
        .then((res)=>{
            if (res.data === "Success"){
                alert("Verified...");
            }
        })
    }
    return (
        <div>
            <h2>Verify Events</h2>
            <div>
                {
                    toVerifylist.map((ele)=>{
                        return(
                            <div key={ele.eventid}>
                                <h4>{ele.ename}</h4>
                                <div>{ele.desc}</div>
                                <div>{ele.edate}</div>
                                <button onClick={() => handleVerifyIt(ele.eventid)}>Verify</button>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default VerifyEvents
