import Axios from 'axios'
import React, { useEffect, useState } from 'react'

function ViewCreatedEvents({ userid }) {
    const [createdList, setCreatedList] = useState([]);
    const [editEvent, setEditevent] = useState(false);
    const [editableEvent, setEditableevent] = useState([]);
    const [changeVenue, setChangeVenue] = useState("");
    const [changeDate, setChangeDate] = useState("");
    const [changeName, setChangeName] = useState("");
    const [changeDesc, setChangeDesc] = useState("");
    const [changeLink, setChangeLink] = useState("");


    useEffect(() => {
        Axios.post('http://localhost:3001/getCreatedEvents', {
            userid: userid
        })
            .then((res) => {
                if (res.data) {
                    setCreatedList(res.data);
                }
            })
    }, [userid, setCreatedList]);
    const handleEventDeleteBtn = (eventid) => {
        Axios.post('http://localhost:3001/deleteEvent', {
            eventid: eventid
        })
            .then((res) => {
                if (res.data === "Success") {
                    alert("Deleted Event...");
                }
            })
    }
    const handleEditEvent = (eventid) => {
        Axios.post('http://localhost:3001/getEventToEdit', {
            eventid: eventid
        })
            .then((res) => {
                if (res.data) {
                    setEditableevent(res.data);
                } else {
                    alert("Cannot Edit...");
                }
            })
        setEditevent(!editEvent);
    }
    const handleSaveEvent = () => {
        Axios.post('http://localhost:3001/editEventDetails',{
            ename:changeName,
            desc:changeDesc,
            
        })
    }
    return (
        <div>
            <h3>View Created Events</h3>
            <div>
                {
                    createdList.map((ele) => {
                        return (
                            <div key={ele.eventid}>
                                <h4>{ele.ename}</h4>
                                <span>{ele.edate}</span>
                                <button onClick={() => handleEditEvent(ele.eventid)}>Edit</button>
                                <button onClick={() => handleEventDeleteBtn(ele.eventid)}>Delete</button>
                            </div>
                        )
                    })
                }
            </div>
            {
                editEvent ?
                    <div>
                        <h5>Edit Event</h5>
                        <input
                            defaultValue={editableEvent.ename}
                            onChange={(e) => {
                                setChangeName(e.target.value);
                            }}
                        />
                        <input
                            defaultValue={editableEvent.desc}
                            onChange={(e) => {
                                setChangeDesc(e.target.value);
                            }}
                        />
                        <input
                            defaultValue={editableEvent.edate}
                            onChange={(e) => {
                                setChangeDate(e.target.value);
                            }}
                        />
                        <input
                            defaultValue={editableEvent.venue}
                            onChange={(e) => {
                                setChangeVenue(e.target.value);
                            }}
                        />
                        <input
                            defaultValue={editableEvent.reglink}
                            onChange={(e) => {
                                setChangeLink(e.target.value);
                            }}
                        />
                    </div>
                    : null
            }
            <button onClick={handleSaveEvent}>Save</button>
        </div>
    )
}

export default ViewCreatedEvents
