import Axios from 'axios'
import React, { useEffect, useState } from 'react'

function ViewCreatedEvents({ userid }) {
    const [createdList, setCreatedList] = useState([]);
    useEffect(() => {
        Axios.post('http://localhost:3001/getCreatedEvents', {
            userid: userid
        })
            .then((res) => {
                if (res.data) {
                    setCreatedList(res.data);
                }
            })
    }, [userid,setCreatedList]);
    const handleEventDeleteBtn = (eventid) => {
        Axios.post('http://localhost:3001/deleteEvent',{
            eventid:eventid
        })
        .then((res)=>{
            if (res.data === "Success"){
                alert("Deleted Event...");
            }
        })
    }
    const handleEditEvent = () => {
        <input/>
    }
    return (
        <div>
            <h3>View Created Events</h3>
            <div>
            {
                createdList.map((ele)=>{
                    return (
                        <div key={ele.eventid}>
                            <h4>{ele.ename}</h4>
                            <span>{ele.edate}</span>
                            <button onClick={handleEditEvent}>Edit</button>
                            <button onClick={() => handleEventDeleteBtn(ele.eventid)}>Delete</button>
                        </div>
                    )
                })
            }
            </div>
        </div>
    )
}

export default ViewCreatedEvents
