import React, { useEffect, useState } from 'react'
import Axios from 'axios';

function UpcomingEvents({search, userid}) {
    const [verifiedList, setVerifiedList] = useState([]);
    useEffect(() => {
        Axios.get('http://localhost:3001/getVerifiedEvents')
            .then((res) => {
                setVerifiedList(res.data);
            })
            .catch((err) => {
                alert(err);
            })
    }, [setVerifiedList]);
    const handleRegistered = (eventid) => {
        Axios.post('http://localhost:3001/addRegister',{
            eventid:eventid,
            userid:userid
        })
        .then((res)=>{
            if (res.data === "Success"){
                alert("Redire");
            }
        })
    }
    return (
        <div>
            <h2>UpcomingEvents</h2>
            <div>
                {
                    verifiedList.filter((item)=>{
                        return search === "" ? item : item.ename.toLowerCase().includes(search);
                    })
                    .map((ele) => {
                        return (
                            <div key={ele.eventid}>
                                <h4>{ele.ename}</h4>
                                <div>{ele.desc}</div>
                                <div>{ele.edate}</div>
                                <a href={`${ele.reglink}`} onClick={() => handleRegistered(ele.eventid)}>Click here to register</a>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default UpcomingEvents
