import React, { useEffect, useState } from 'react'

function UpdateUser() {
    const [toUpdate, setToupdate] = useState(false);
    const [toSearchUser, setTosearchuser] = useState("");
    const handleToUpdateCheck = () => {
        setToupdate(!toUpdate);
    }
    const [verifiedList, setVerifiedList] = useState([]);
    useEffect(() => {
        Axios.get('http://localhost:3001/getVerifiedEvents')
            .then((res) => {
                setVerifiedList(res.data);
            })
            .catch((err) => {
                alert(err);
            })
    }, [setVerifiedList]);
    return (
        <div>
            <button onClick={handleToUpdateCheck}>Update User</button>
            {
                toUpdate ?
                    <input
                        placeholder='Search user...'
                        type='text'
                        onChange={(e) => {
                            setTosearchuser(e.target.value);
                        }}
                    />
                    :
                    null
            }
        </div>
    )
}

export default UpdateUser
