import React, { useState } from 'react';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../style/Login.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Login() {
  const navigate = useNavigate();
  const [userid, setUserid] = useState("");
  const [pass, setPass] = useState("");
  const handleLoginBtn = () => {
    if (userid !== "" && pass !== "") {
      Axios.post('http://localhost:3001/checkLogin', {
        userid: userid,
        pass: pass
      }).then((res) => {
        if (res.data === "Error") {
          alert("Invalid Credentials..");
        } else if (res.data) {
          alert("Login Successful...");
          if (res.data.role === 'admin') {
            navigate('/admin');
          } else {
            let userdata = res.data;
            navigate('/home', { state: { userdata } });
          }
        }
      })
    } else {
      alert("Fill all the details...");
    }
  }
  const handleNewAccountBtn = () => {
    navigate('/signup');
  }
  return (
    <div className='login-cont'>
      <div className='login-form'>
        <h1 className='title'>CampusHive</h1>
        <input
          placeholder='Enter User ID'
          type='text'
          name='userid'
          onChange={(e) => {
            setUserid(e.target.value);
          }}
          autoComplete='off'
        />
        <input
          placeholder='Enter Password'
          type='password'
          name='pass'
          onChange={(e) => {
            setPass(e.target.value);
          }}
          autoComplete='off'
        />
        <button className='loginbtn' onClick={handleLoginBtn}>Login</button>
        <button className='signupbtn' onClick={handleNewAccountBtn}>New User? Click here</button>
      </div>
      <ToastContainer />
    </div>
  )
}

export default Login
