import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import Schedule from '../components/Schedule';
import UpcomingEvents from '../components/UpcomingEvents';

function Home() {

    const navigate = useNavigate();
    const location = useLocation();
    const [userdata,setUserdata] = useState([]);
    const [toDisplayschedule, setTodisplayscheule] = useState(false);

    useEffect(()=>{
        setUserdata(location.state.userdata);
    },[location,setUserdata]);

    useEffect(()=>{
        if (userdata.role === "placement-incharge" || userdata.role === "club-head" || userdata.role === "alumni"){
            setTodisplayscheule(true);
        } else if (userdata.role === "student") {
            setTodisplayscheule(false);
        }
    },[userdata,setTodisplayscheule]);

    const handleTitleClick = () => {
        navigate('/home', { state: { userdata } });
    }

    const handleProfileClick = () => {
        navigate('/profile', { state: { userdata } });
    }

    const handleLogout = () => {
        navigate('/');
    }

    return (
        <div>
            <div className='navbar'>
                <div className='home-website-title' onClick={handleTitleClick}>CampusHive</div>
                <div className='left-menu'>
                    <button className='profile-btn' onClick={handleProfileClick}><img alt='Profile' src={require('../images/prof-icon.png')} className='icon' /></button>
                    <button className='logout-btn' onClick={handleLogout}><img alt='Logout' src={require('../images/log-icon.png')} className='icon' /></button>
                </div>
                <h2>Welcome, {userdata.fname}</h2>
                <div>
                    
                </div>
                <div>{toDisplayschedule?<Schedule/>:null}</div>
                <div>{<UpcomingEvents/>}</div>
            </div>
        </div>
    )
}

export default Home
