import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Schedule() {
    const navigate = useNavigate();
    const [eventid, setEventid] = useState("");
    const [ename, setEname] = useState("");
    const [creatorid, setCreatorid] = useState("");
    const [desc, setDesc] = useState("");
    const [edate, setEdate] = useState("");
    const [venue, setVenue] = useState("");
    const [reglink, setReglink] = useState("");
    const [verified, setVerified] = useState(false);
    const [checkEventPresent, setCheckeventpresent] = useState(false);
    useEffect(() => {
        setVerified(false);
    }, [setVerified]);
    const handleCreateEventBtn = () => {
        if (eventid !== "" && ename !== "" && creatorid !== "" && desc !== "" && edate !== "" && venue !== "" && reglink !== "") {
            Axios.post('http://localhost:3001/checkEvent', {
                eventid: eventid
            }).then((res) => {
                if (res.data === "Success") {
                    alert("Event already added...");
                } else if (res.data === "Error") {
                    setCheckeventpresent(false);
                }
            })
            if (!checkEventPresent) {
                Axios.post('http://localhost:3001/addEvent', {
                    eventid: eventid,
                    ename: ename,
                    creatorid: creatorid,
                    desc: desc,
                    edate: e,
                    venue: String,
                    reglink: String,
                    verified: Boolean
                })
            }
        } else {
            alert("Enter all details...");
        }
    }
    return (
        <div>
            <h2>Schedule Events</h2>
            <div className='signup-form'>
                <input
                    required
                    placeholder='Enter eventid'
                    type='text'
                    name='eventid'
                    onChange={(e) => {
                        setEventid(e.target.value);
                    }}
                />
                <input
                    placeholder='Enter event name'
                    type='text'
                    name='ename'
                    onChange={(e) => {
                        setEname(e.target.value);
                    }}
                />
                <input
                    placeholder='Enter creatorid'
                    type='text'
                    name='creatorid'
                    onChange={(e) => {
                        setCreatorid(e.target.value);
                    }}
                />
                <input
                    placeholder='Enter event description'
                    type='text'
                    name='desc'
                    onChange={(e) => {
                        setDesc(e.target.value);
                    }}
                />
                <input
                    placeholder='Enter event date'
                    type='text'
                    name='edate'
                    onChange={(e) => {
                        setEdate(e.target.value);
                    }}
                />
                <input
                    placeholder='Enter registeration link'
                    type='text'
                    name='reglink'
                    onChange={(e) => {
                        setReglink(e.target.value);
                    }}
                />
                <input
                    placeholder='Enter venue'
                    type='text'
                    name='venue'
                    onChange={(e) => {
                        setVenue(e.target.value);
                    }}
                />
                <button onClick={handleCreateEventBtn}>Create Event</button>
            </div>
        </div>
    )
}

export default Schedule
