import React, { useState } from 'react'
import { useNavigate, useLocation } from 'react-router-dom';
import RegisteredEvents from '../components/RegisteredEvents';
import ProfileEdit from '../components/ProfileEdit';


function Profile() {
  const navigate = useNavigate();
  const location = useLocation();
  const userdata = location.state.userdata;
  const [isEditable,setIseditable] = useState(false);

  const handleTitleClick = () => {
    navigate('/home', { state: { userdata } });
  }

  const handleLogout = () => {
    navigate('/');
  }

  const handleEditableFunction = () => {
    setIseditable(!isEditable);
  }

  return (
    <div>
      <div className='navbar'>
        <div className='home-website-title' onClick={handleTitleClick}>CampusHive</div>
        <div className='left-menu'>
          <button className='logout-btn' onClick={handleLogout}><img alt='Logout' src={require('../images/log-icon.png')} className='icon' /></button>
        </div>
      </div>
      <div>
        <h3>Welcome, {userdata.fname}</h3>
        <div>
          <h3>Registered Events</h3>
          <RegisteredEvents userid={userdata.userid} />
        </div>
        <div>
          <h3></h3>
        </div>
        <button onClick={handleEditableFunction}>Edit Profile</button>
        {
          isEditable ? <ProfileEdit userdata={userdata}/> : null
        }
      </div>
    </div>
  )
}

export default Profile
