import React, { useEffect, useState } from 'react'
import Axios from 'axios';
import { useLocation } from 'react-router-dom';
import '../style/Event.css';

function Event() {
  const location = useLocation();
  const [eventData, setEventdata] = useState([]);
  const [userid, setUserid] = useState("");
  const [isAdmin, setIsadmin] = useState(false);
  useEffect(() => {
    setEventdata(location.state.event);
    setUserid(location.state.userid);
    if (userid === "admin") {
      setIsadmin(true);
    }
  }, [setEventdata, setUserid, location, setIsadmin, userid]);
  const handleRegistered = () => {
    Axios.post('http://localhost:3001/addRegister', {
      event: eventData,
      userid: userid
    })
      .then((res) => {
        if (res.data === "Success") {
          alert("Redirecting to Orignal website...");
        } else {
          alert("Do not register...");
        }
      })
  }
  return (
    <div className='event-cont'>
      <div className='event-data'>
        <div className='event-details'>
          <h1>{eventData.ename}</h1>
          <h4>{eventData.eventid.toUpperCase()}</h4>
          <hr></hr>
          <div>
            <h4>Description</h4>
            <div>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker incl</div>
          </div>
          <div>
            <h4>Date Of Event</h4>
            <div>{eventData.edate}</div>
          </div>
          <div></div>
          {
            isAdmin ? null : <a href={`${eventData.reglink}`} onClick={handleRegistered}>Click here to Register</a>
          }
          <div>Venue <span>{eventData.venue}</span></div>
        </div>

      </div>
    </div>
  )
}

export default Event
